import React from 'react'
import { Link, NavLink } from "react-router-dom";
import './Header.css'
import "./";
import {useContext } from "react";
 import ThemeContext from "../theme/themeContext";
 import { useAuthState } from 'react-firebase-hooks/auth';
 import {auth} from '../firebase/config';
 import { signOut } from "firebase/auth"

function Header() {
  const [user ] = useAuthState(auth);
  const { theme ,toggleTheme} = useContext(ThemeContext);
  return (
    <div className='myheader'>
  
        <header className="hide-when-mobile">
    <h1><Link to ="/">Amel.dev</Link></h1>
    <i
          onClick={() => {
            toggleTheme(theme === "Light" ? "Dark" : "Light");
          }}
          className="fa-solid fa-moon"
        ></i>
        <i
          onClick={() => {
            toggleTheme(theme === "Light" ? "Dark" : "Light");
          }}
          className="fa-solid fa-sun"
        ></i>
      
    <ul className="flex">
      {!user && <li className="main-list">
        <NavLink className="main-link" to="/singin"> Singin </NavLink>
      </li>
      }
        
  
        {!user &&  <li className="main-list">
        <NavLink className="main-link" to="/singup"> Singup </NavLink>
    
      </li>      }
        
      {user && <li onClick={() => {signOut(auth).then(() => {

     console.log('Sign-out successful.')  
}).catch((error) => {
  // An error happened.
});  }}  className="main-list">
        <NavLink className="main-link" to="/singin"> Singout </NavLink>
      </li>
      }
      {user &&  <li className="main-list">
        <NavLink className="main-link" to="/About"> About </NavLink>
    
      </li> }
      
      {user && <li className="main-list">
        <NavLink className="main-link" to="/Porfile"> Portfile  </NavLink>
        
          </li>
      
}

    
    
  
    
    </ul>
  </header>

  <header className="show-when-mobile">
    <h1>Amel.DEV </h1>
    <label className="absolute" htmlFor="burger">
      <i className="fas fa-bars" />
    </label>
    <input id="burger" type="checkbox" />
    <div className="show-on-click">
      <div className="main-div">
        <label htmlFor="html">HTML <i className="fas fa-plus" /> </label>
        <input id="html" type="checkbox" />
        <ul className="sub-div">
          <li><a href>Full Course</a></li>
          <li><a href>Crash Course</a></li>
          <li><a href>learn in 1h</a></li>
        </ul>
      </div>
      <div className="main-div">
        <label htmlFor="css">CSS <i className="fas fa-plus" /> </label>
        <input id="css" type="checkbox" />
        <ul className="sub-div">
          <li><a href>Full Course</a></li>
          <li><a href>CSS Examples</a></li>
          <li>
            <label className="mini-projects" htmlFor="mini">mini projects <i className="fas fa-plus" /> </label>
            <input id="mini" type="checkbox" />
            <ul className="sub-sub-div">
              <li><a href>project 1</a></li>
              <li><a href>project 2</a></li>
              <li><a href>project 3</a></li>
            </ul>
          </li>
        </ul>
      </div>
      <div className="main-div">
        <label htmlFor="js">JavaScript <i className="fas fa-plus" /> </label>
        <input id="js" type="checkbox" />
        <ul className="sub-div">
          <li><a href>coming soonðŸ”¥</a></li>
        </ul>
      </div>
    </div>
  </header>
    </div>
  )
}

export default Header
